  sales_count: number;
  average_transaction: number;
  daily_growth: number;
  weekly_growth: number;
  monthly_growth: number;
  top_products: Array<{
    product: string;
    sales: number;
    revenue: number;
    growth: number;
  }>;
  payment_methods: Array<{
    method: string;
    count: number;
    amount: number;
    percentage: number;
  }>;
  hourly_sales: Array<{
    hour: number;
    sales: number;
    transactions: number;
  }>;
}

interface Sale {
  id: string;
  transaction_id: string;
  customer_name: string;
  customer_email?: string;
  products: Array<{
    name: string;
    quantity: number;
    unit_price: number;
    total: number;
  }>;
  subtotal: number;
  tax: number;
  total: number;
  payment_method: string;
  status: 'completed' | 'pending' | 'cancelled' | 'refunded';
  created_at: string;
  cashier_name: string;
  notes?: string;
}

interface Customer {
  id: string;
  name: string;
  email: string;
  phone: string;
  total_purchases: number;
  total_spent: number;
  last_purchase: string;
  loyalty_points: number;
  tier: 'bronze' | 'silver' | 'gold' | 'platinum';
  created_at: string;
}

interface Product {
  id: string;
  name: string;
  category: string;
  price: number;
  cost: number;
  profit_margin: number;
  stock_quantity: number;
  units_sold: number;
  revenue: number;
  last_sold: string;
}

// Sales service for Supabase integration
const salesService = {
  getMetrics: async (): Promise<SalesMetrics> => {
    const { data, error } = await supabase.functions.invoke('sales-analytics', {
      body: { action: 'get_metrics' }
    });
    if (error) throw error;
    return data;
  },

  getSales: async (): Promise<Sale[]> => {
    const { data, error } = await supabase.functions.invoke('sales-analytics', {
      body: { action: 'get_sales' }
    });
    if (error) throw error;
    return data;
  },

  getCustomers: async (): Promise<Customer[]> => {
    const { data, error } = await supabase.functions.invoke('sales-analytics', {
      body: { action: 'get_customers' }
    });
    if (error) throw error;
    return data;
  },

  getProducts: async (): Promise<Product[]> => {
    const { data, error } = await supabase.functions.invoke('sales-analytics', {
      body: { action: 'get_products' }
    });
    if (error) throw error;
    return data;
  },

  createSale: async (saleData: Partial<Sale>) => {
    const { data, error } = await supabase.functions.invoke('sales-analytics', {
      body: { action: 'create_sale', ...saleData }
    });
    if (error) throw error;
    return data;
  },

  refundSale: async (saleId: string) => {
    const { data, error } = await supabase.functions.invoke('sales-analytics', {
      body: { action: 'refund_sale', sale_id: saleId }
    });
    if (error) throw error;
    return data;
  }
};

