import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { toast } from "sonner";
import {
  Sale,
  CreateSaleRequest,
  UpdateSaleRequest,
  SalesFilters,
  SalesExportOptions,
} from "../types";
import { getSales, getSaleById, createSale, updateSale, deleteSale } from "../services";

// Adapter functions to transform between feature types and centralized service types
const adaptCreateSaleRequest = (request: CreateSaleRequest) => ({
  customer_name: request.customerName,
  fuel_type: request.fuelType,
  quantity: request.quantityLiters,
  price_per_liter: request.unitPrice,
  total_amount: request.amount,
  sale_date: request.saleDate instanceof Date ? request.saleDate.toISOString() : request.saleDate,
  filling_system_id: request.fillingSystemId,
  payment_method: request.paymentMethod as "cash" | "card" | "credit",
  status: "active" as const,
});

const adaptUpdateSaleRequest = (request: UpdateSaleRequest) => ({
  customer_name: request.customerName,
  fuel_type: request.fuelType,
  quantity: request.quantityLiters,
  price_per_liter: request.unitPrice,
  total_amount: request.amount,
  sale_date: request.saleDate instanceof Date ? request.saleDate.toISOString() : request.saleDate,
  filling_system_id: request.fillingSystemId,
  payment_method: request.paymentMethod as "cash" | "card" | "credit",
});

export const salesKeys = {
  all: ["sales"] as const,
  lists: () => [...salesKeys.all, "list"] as const,
  list: (filters: SalesFilters) => [...salesKeys.lists(), filters] as const,
  details: () => [...salesKeys.all, "detail"] as const,
  detail: (id: string) => [...salesKeys.details(), id] as const,
};

export function useSalesQuery(filters?: SalesFilters) {
  return useQuery({
    queryKey: salesKeys.list(filters || {}),
    queryFn: () => getSales(filters

),
    staleTime: 5 * 60 * 1000, // 5 minutes
  });
}

export function useSaleQuery(id: string) {
  return useQuery({
    queryKey: salesKeys.detail(id),
    queryFn: () => getSaleById(id),
    enabled: !!id,
    staleTime: 5 * 60 * 1000, // 5 minutes
  });
}

export function useSalesMutations() {
  const queryClient = useQueryClient();

  const createSaleMutation = useMutation({
    mutationFn: (newSale: CreateSaleRequest) =>
      createSale

,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: salesKeys.lists() });
      toast.success("Sale created successfully");
    },
    onError: () => {
      toast.error("Failed to create sale");
    },
  });

  const updateSaleMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: UpdateSaleRequest }) =>
      updateSale

,
    onSuccess: (_, variables) => {
      queryClient.invalidateQueries({ queryKey: salesKeys.lists() });
      queryClient.invalidateQueries({
        queryKey: salesKeys.detail(variables.id),
      });
      toast.success("Sale updated successfully");
    },
    onError: () => {
      toast.error("Failed to update sale");
    },
  });

  const deleteSaleMutation = useMutation({
    mutationFn: (id: string) => deleteSale(id),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: salesKeys.lists() });
      toast.success("Sale deleted successfully");
    },
    onError: () => {
      toast.error("Failed to delete sale");
    },
  });

  const exportSalesMutation = useMutation({
    mutationFn: (options: SalesExportOptions) => {
      throw new Error("Export functionality not yet implemented in centralized service");
    },
    onSuccess: () => {
      toast.success("Sales data exported successfully");
    },
    onError: () => {
      toast.error("Failed to export sales data");
    },
  });

  return {
    createSale: createSaleMutation,
    updateSale: updateSaleMutation,
    deleteSale: deleteSaleMutation,
    exportSales: exportSalesMutation,
  };
}

export function useExportSales() {
  const { exportSales } = useSalesMutations();

  const exportSalesData = async (options: SalesExportOptions) => {
    try {
      const data = await exportSales.mutateAsync(options);

      // Handle CSV data
      if (options.format === "csv" || !options.format) {
        const blob = new Blob([data as string], { type: "text/csv" });
        const url = URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = `sales-export-${new Date().toISOString().split("T")[0]}.csv`;
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
      }
    } catch (error) {
      console.error("Error in export function:", error);
    }
  };

  return {
    exportSalesData,
    isExporting: exportSales.isPending,
  };
}

export default function useSales() {
  return {
    useSalesQuery,
    useSaleQuery,
    useSalesMutations,
    useExportSales,
  };
}

import { useState, useCallback, useEffect } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { salesApi, Sale } from "@/core/api";
import { useToast } from "@/hooks";

// Define the update request interface based on the Sale type
interface UpdateSaleRequest {
  filling_system_id?: string;
  fuel_type_id?: string;
  quantity?: number;
  price_per_liter?: number;
  total_price?: number;
  payment_method?: string;
  employee_id?: string;
  shift_id?: string;
  comments?: string;
}

export function useSalesMutations() {
  // State declarations
  const [selectedSale, setSelectedSale] = useState<Sale | null>(null);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [isDeleteDialogOpen, setIsDeleteDialogOpen] = useState(false);
  const [saleToDelete, setSaleToDelete] = useState<string | null>(null);

  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Define mutations
  const deleteMutation = useMutation({
    mutationFn: salesApi.deleteSale,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["sales"] });
      queryClient.invalidateQueries({ queryKey: ["fuel-tanks"] });

      toast({
        title: "Success",
        description: "Sale deleted successfully and tank level restored",
      });
      setIsDeleteDialogOpen(false);
      setSaleToDelete(null);
    },
    onError: (error) => {
      console.error("Delete error:", error);
      toast({
        title: "Error",
        description: "Failed to delete sale",
        variant: "destructive",
      });
    },
  });
  const updateMutation = useMutation({
    mutationFn: (params: { id: string; updates: UpdateSaleRequest }) =>
      salesApi.updateSale(params.id, params.updates),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["sales"] });
      queryClient.invalidateQueries({ queryKey: ["fuel-tanks"] });
      queryClient.invalidateQueries({ queryKey: ["latest-sale"] });

      toast({
        title: "Success",
        description: "Sale updated successfully and tank level adjusted",
      });
      setIsEditDialogOpen(false);
      setSelectedSale(null);
    },
    onError: (error) => {
      console.error("Update error:", error);
      toast({
        title: "Error",
        description: "Failed to update sale",
        variant: "destructive",
      });
    },
  });

  // Effect to reset state when dialogs close
  useEffect(() => {
    if (!isEditDialogOpen) {
      // Only reset selected sale when edit dialog closes
      setSelectedSale(null);
    }
  }, [isEditDialogOpen]);

  useEffect(() => {
    if (!isDeleteDialogOpen) {
      // Only reset sale to delete when delete dialog closes
      setSaleToDelete(null);
    }
  }, [isDeleteDialogOpen]);

  // Handler functions
  const handleEdit = useCallback((sale: Sale) => {
    setSelectedSale(sale);
    setIsEditDialogOpen(true);
  }, []);

  const handleDelete = useCallback((id: string) => {
    setSaleToDelete(id);
    setIsDeleteDialogOpen(true);
  }, []);

  const confirmDelete = useCallback(() => {
    if (saleToDelete) {
      deleteMutation.mutate(saleToDelete);
    }
  }, [saleToDelete, deleteMutation]);

  return {
    selectedSale,
    setSelectedSale,
    isEditDialogOpen,
    setIsEditDialogOpen,
    isDeleteDialogOpen,
    setIsDeleteDialogOpen,
    saleToDelete,
    setSaleToDelete,
    deleteMutation,
    updateMutation,
    handleEdit,
    handleDelete,
    confirmDelete,
  };
}

import { useState, useCallback, useMemo } from "react";
import { useSearchParams } from "react-router-dom";
import { useTranslation } from "react-i18next";
import { FuelTypeCode, PaymentStatus } from "@/types";
import { SalesFilters } from "../types";

interface UseSalesFiltersProps {
  defaultFilters?: Partial<SalesFilters>;
}

export function useSalesFilters({
  defaultFilters = {},
}: UseSalesFiltersProps = {}) {
  const { t } = useTranslation(["common", "sales"]);
  const [searchParams, setSearchParams] = useSearchParams();

  // Initialize filters from URL params or defaults
  const initialFilters: SalesFilters = useMemo(() => {
    const searchTerm =
      searchParams.get("search") || defaultFilters.searchTerm || "";
    const fuelType = (searchParams.get("fuelType") ||
      defaultFilters.fuelType ||
      "all") as FuelTypeCode | "all";
    const paymentStatus = (searchParams.get("paymentStatus") ||
      defaultFilters.paymentStatus ||
      "all") as PaymentStatus | "all";
    const fillingSystem =
      searchParams.get("fillingSystem") ||
      defaultFilters.fillingSystem ||
      "all";

    // Handle date range
    let dateRange: SalesFilters["dateRange"] = undefined;
    const startDate = searchParams.get("startDate");
    const endDate = searchParams.get("endDate");

    if (startDate) {
      dateRange = {
        from: new Date(startDate),
        to: endDate ? new Date(endDate) : undefined,
      };
    } else if (defaultFilters.dateRange) {
      dateRange = defaultFilters.dateRange;
    }

    // Handle min/max quantity
    const minQuantity = searchParams.get("minQuantity")
      ? Number(searchParams.get("minQuantity"))
      : defaultFilters.minQuantity;

    const maxQuantity = searchParams.get("maxQuantity")
      ? Number(searchParams.get("maxQuantity"))
      : defaultFilters.maxQuantity;

    // Handle min/max amount
    const minAmount = searchParams.get("minAmount")
      ? Number(searchParams.get("minAmount"))
      : defaultFilters.minAmount;

    const maxAmount = searchParams.get("maxAmount")
      ? Number(searchParams.get("maxAmount"))
      : defaultFilters.maxAmount;

    return {
      searchTerm,
      dateRange,
      fuelType,
      paymentStatus,
      fillingSystem,
      minQuantity,
      maxQuantity,
      minAmount,
      maxAmount,
    };
  }, [searchParams, defaultFilters]);

  const [filters, setFilters] = useState<SalesFilters>(initialFilters);

  // Sync filters with URL params
  const updateFilters = useCallback(
    (newFilters: Partial<SalesFilters>) => {
      const updatedFilters = { ...filters, ...newFilters };
      setFilters(updatedFilters);

      // Update URL params
      const params = new URLSearchParams();

      if (updatedFilters.searchTerm)
        params.set("search", updatedFilters.searchTerm);
      if (updatedFilters.fuelType && updatedFilters.fuelType !== "all") {
        params.set("fuelType", updatedFilters.fuelType);
      }
      if (
        updatedFilters.paymentStatus &&
        updatedFilters.paymentStatus !== "all"
      ) {
        params.set("paymentStatus", updatedFilters.paymentStatus);
      }
      if (
        updatedFilters.fillingSystem &&
        updatedFilters.fillingSystem !== "all"
      ) {
        params.set("fillingSystem", updatedFilters.fillingSystem);
      }

      // Handle date range
      if (updatedFilters.dateRange?.from) {
        params.set(
          "startDate",
          updatedFilters.dateRange.from.toISOString().split("T")[0]
        );

        if (updatedFilters.dateRange.to) {
          params.set(
            "endDate",
            updatedFilters.dateRange.to.toISOString().split("T")[0]
          );
        }
      }

      // Handle min/max quantity
      if (updatedFilters.minQuantity)
        params.set("minQuantity", updatedFilters.minQuantity.toString());
      if (updatedFilters.maxQuantity)
        params.set("maxQuantity", updatedFilters.maxQuantity.toString());

      // Handle min/max amount
      if (updatedFilters.minAmount)
        params.set("minAmount", updatedFilters.minAmount.toString());
      if (updatedFilters.maxAmount)
        params.set("maxAmount", updatedFilters.maxAmount.toString());

      setSearchParams(params);
    },
    [filters, setSearchParams]
  );

  const resetFilters = useCallback(() => {
    setFilters(defaultFilters as SalesFilters);
    setSearchParams(new URLSearchParams());
  }, [defaultFilters, setSearchParams]);

  // Predefined filter options
  const filterOptions = useMemo(() => {
    return {
      fuelTypes: [
        { value: "all", label: t("common:all") },
        { value: "diesel", label: t("sales:fuelTypes.diesel") },
        { value: "gas", label: t("sales:fuelTypes.gas") },
        { value: "petrol_regular", label: t("sales:fuelTypes.petrol_regular") },
        { value: "petrol_premium", label: t("sales:fuelTypes.petrol_premium") },
      ],
      paymentStatuses: [
        { value: "all", label: t("common:all") },
        { value: "pending", label: t("sales:paymentStatuses.pending") },
        { value: "completed", label: t("sales:paymentStatuses.completed") },
        { value: "cancelled", label: t("sales:paymentStatuses.cancelled") },
        { value: "paid", label: t("sales:paymentStatuses.paid") },
        { value: "failed", label: t("sales:paymentStatuses.failed") },
        { value: "refunded", label: t("sales:paymentStatuses.refunded") },
      ],
    };
  }, [t]);

  return {
    filters,
    updateFilters,
    resetFilters,
    filterOptions,
  };
}

